class Product {
  final String id;
  final String name;
  final String brand;
  final double price;
  final String size;
  final String imageUrl;
  final String category;
  final int stock;

  Product({
    required this.id,
    required this.name,
    required this.brand,
    required this.price,
    required this.size,
    required this.imageUrl,
    required this.category,
    required this.stock,
  });
}

